mapper.select:
select *,DATE_FORMAT(qdate,'%Y-%m-%d') as qdate1,DATE_FORMAT(opdate,'%Y-%m-%d') as opdate1 from quit
where 1=1

  		and sname like concat('%',#{sname},'%') 
  
  
  		and staff_id = #{staff_id} 
  
  
  		and qtype = #{qtype} 
  
  
  		and date(qdate ) between #{startdate} and #{enddate}
