mapper.select:
select *,DATE_FORMAT(s.entrydate,'%Y-%m-%d') as entrydate from staff s, department d, post p where s.depart_id = d.id and s.post_id = p.id
and date(s.entrydate ) between #{startdate} and #{enddate}
order by entrydate desc

select *,DATE_FORMAT(q.qdate,'%Y-%m-%d') as qdate from staff s, department d, post p, quit q where s.depart_id = d.id and s.post_id = p.id and q.staff_id = s.id
and date(q.qdate ) between #{startdate} and #{enddate}
order by qdate desc

select *,DATE_FORMAT(t.tdate,'%Y-%m-%d') as tdate, p1.pname as pname1, p2.pname as pname2 from staff s, department d, post p1, post p2, transfer t where t.staff_id = s.id and s.depart_id = d.id and t.beforepost_id = p1.id and t.afterpost_id = p2.id
and date(t.tdate ) between #{startdate} and #{enddate}
order by tdate desc
