mapper.select:
select *,DATE_FORMAT(s.startdate,'%Y-%m-%d') as startdate1 ,DATE_FORMAT(s.enddate,'%Y-%m-%d') as enddate1  from staff s, department d, post p where s.depart_id = d.id and s.post_id = p.id and s.status = '转正' 
  
  		and s.sname like concat('%',#{sname},'%') 
  
  
  		and s.depart_id = #{depart_id} 
  
  
  		and date(s.startdate ) between #{startdate} and #{enddate} 
  		and date(s.enddate ) between #{startdate} and #{enddate}

select *,DATE_FORMAT(s.birthday,'%Y-%m-%d') as birthday1,DATE_FORMAT(s.entrydate,'%Y-%m-%d') as entrydate1,
DATE_FORMAT(s.joinworkdate,'%Y-%m-%d') as joinworkdate1,DATE_FORMAT(s.graduationdate,'%Y-%m-%d') as graduationdate1
,DATE_FORMAT(s.startdate,'%Y-%m-%d') as startdate1 ,DATE_FORMAT(s.enddate,'%Y-%m-%d') as enddate1  from staff s, department d, post p where s.id = #{id} and s.depart_id = d.id and s.post_id = p.id;

select * from staff s, department d, post p where s.depart_id = d.id and s.post_id = p.id 

  		and s.sname like concat('%',#{sname},'%') 
  
  
  		and s.depart_id = #{depart_id}

select * from staff s, department d, post p where s.depart_id = d.id and s.post_id = p.id 
 
  		and s.sname like concat('%',#{sname},'%') 
 
 
  		and s.depart_id = #{depart_id} 
 
 
 		and s.post_id = #{post_id} 
 
 
  		and s.status = #{status} 
 
 
  		and date(s.startdate ) between #{startdate} and #{enddate} 
  		and date(s.enddate ) between #{startdate} and #{enddate}

mapper.update:
update staff
status = #{status}, peroidopdate = now()
where id=#{id}

update staff
sname = #{sname}, sex = #{sex}, birthday = #{birthday}, sid = #{sid}, depart_id = #{depart_id}, post_id = #{post_id}, entrydate = #{entrydate}, joinworkdate = #{joinworkdate}, workform = #{workform}, staffsource = #{staffsource}, politicalstatus = #{politicalstatus}, nation = #{nation}, nativeplace = #{nativeplace}, stel = #{stel}, semail = #{semail}, sheight = #{sheight}, bloodtype = #{bloodtype}, maritalstatus = #{maritalstatus}, registeredresidence = #{registeredresidence}, education = #{education}, degree = #{degree}, university = #{university}, major = #{major}, graduationdate = #{graduationdate}, startdate = #{startdate}, enddate = #{enddate}
where id = #{id}
